package codeup;

import java.util.Scanner;

public class Q1062 {

	// 1062 : [기초-비트단위논리연산] 비트단위로 XOR 하여 출력하기(설명)
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner s = new Scanner(System.in);

		int a = s.nextInt();
		int b = s.nextInt();

		System.out.printf("%d", a ^ b);
	}

}
