package codeup;

import java.util.Scanner;

public class Q1075 {
	// 1075 : [기초-반복실행구조] 정수 1개 입력받아 카운트다운 출력하기2(설명)
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner s = new Scanner(System.in);
		int num = s.nextInt();
		while (num-- != 0) {
			System.out.println(num);
		}

	}
}
